import { useContext, useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { Context } from '../context'
import { EpisodeCharacters } from './EpisodeCharacters'
function Episode() {
    const { darkTheme, show, setShow } = useContext(Context)
    const [episode, setEpisode] = useState([])
    const { id } = useParams()
    const navigate = useNavigate()
    const arr = []

    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/episode/${id}`)
            .then((response) => response.json())
            .then((data) => setEpisode(data))
        // eslint-disable-next-line
    }, [])
    if (episode.name) {
        const getCharacters = async () => {
            episode.characters.forEach((element) => {
                fetch(`https://rickandmortyapi.com/api/character/${element.slice(42)}`)
                    .then((response) => response.json())
                    .then((data) => arr.push(data))
            })
        }
    }
    console.log(arr)

    const goBack = () => navigate(-1)

    return (
        <>
            {!episode.name ? (
                <h6 className="loading">Loading</h6>
            ) : (
                <div className="single-character">
                    <button
                        className={darkTheme ? 'goback black-button' : 'goback white-button'}
                        onClick={goBack}>
                        Go back
                    </button>
                    <p>Episode name: {episode.name}</p>
                    <p>Air date: {episode.air_date}</p>
                    <button onClick={() => setShow(!show)}>click</button>
                    {show && arr.length
                        ? arr.map((item) => <EpisodeCharacters key={item.id} {...item} />)
                        : null}
                </div>
            )}
        </>
    )
}

export { Episode }
