import { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Context } from '../context'

function CharacherCard() {
    const { darkTheme } = useContext(Context)
    const { id } = useParams()
    const [character, setCharacter] = useState([])

    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/character/${id}`)
            .then((response) => response.json())
            .then((data) => setCharacter(data))
    }, [id])
    console.log(character)
    return (
        <>
            {character.length ? (
                <h3>Nothing found</h3>
            ) : (
                <div className="single-character">
                    <div className="picture">
                        <img src={character.image} alt="" />
                    </div>
                    <div className={darkTheme ? 'description' : 'description-dark'}>
                        <p className="country-name">Name: {character.name}</p>
                        <p className="country-name">Status: {character.status}</p>
                        <p className="country-name">Gender: {character.gender}</p>
                    </div>
                </div>
            )}
        </>
    )
}

export { CharacherCard }
