import { useContext, useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { Context } from '../context'
function Episode() {
    const { darkTheme } = useContext(Context)
    const [episode, setEpisode] = useState([])
    const [characters, setCharacters] = useState()
    const { id } = useParams()
    const navigate = useNavigate()

    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/episode/${id}`)
            .then((response) => response.json())
            .then((data) => setEpisode(data))
        // eslint-disable-next-line
    }, [])

    const goBack = () => navigate(-1)
    return (
        <>
            {!episode.name ? (
                <h6 className="loading">Loading</h6>
            ) : (
                <div className="single-character">
                    <button
                        className={darkTheme ? 'goback black-button' : 'goback white-button'}
                        onClick={goBack}>
                        Go back
                    </button>
                    <p>Episode name: {episode.name}</p>
                    <p>Air date: {episode.air_date}</p>
                    <div className="cart">
                        {episode.characters.forEach((element) => {
                            fetch(element)
                                .then((response) => response.json())
                                .then((data) => setEpisode(data))
                        })}
                        {/* <div className="picture">
                            <img className="character-picture" src={image} alt="" />
                        </div>
                        <div className={darkTheme ? 'description' : 'description-dark'}>
                            <p className="country-name">Name: {name}</p>
                            <p className="country-name">Status: {status}</p>
                            <p className="country-name">Gender: {gender}</p>
                        </div> */}
                    </div>
                </div>
            )}
        </>
    )
}

export { Episode }
