    //             .then((response) => response.json())
    //             .then((data) => setCharacter(data, ...character))
    //     })
    //     console.log(character)
    // }, [])

    arr.forEach((element) => {
        fetch(`https://rickandmortyapi.com/api/character/${element}`)
            .then((response) => response.json())
            .then((data) => char.push(data))
    })
    console.log(char)

    return <>{char.length ? char.map((item) => <CharacterList key={item.id} {...item} />) : null}</>
}

export { EpisodeCharacters }
