import { useContext } from 'react'
import { Context } from '../context'

function SearchFilter() {
    const {
        darkTheme,
        filterSelect,
        setFilterSelect,
        search,
        setSearch,
        setFilterStatus,
        setFilterGender,
    } = useContext(Context)

    const handleSearch = (event) => {
        setSearch(event.target.value)
    }

    const handleFilter = (event) => {
        if (
            event.target.value === 'alive' ||
            event.target.value === 'dead' ||
            event.target.value === 'unknown'
        ) {
            setFilterStatus(true)
            setFilterGender(false)
        }
        if (
            event.target.value === 'male' ||
            event.target.value === 'female' ||
            event.target.value === 'genderless'
        ) {
            setFilterGender(true)
            setFilterStatus(false)
        }
        if (event.target.value === '' || event.target.value === undefined) {
            setFilterGender(false)
            setFilterStatus(false)
        }
        setFilterSelect(event.target.value)
    }

    return (
        <div className="input-filter">
            <input
                className={darkTheme ? 'input-black' : 'input-white'}
                type="search"
                placeholder="Search character..."
                onChange={handleSearch}
                value={search}
            />
            <select
                className={darkTheme ? '' : 'select-dark'}
                value={filterSelect}
                onChange={handleFilter}>
                <option value="filter" selected>
                    Filter
                </option>
                <optgroup label="Status">
                    <option value="alive">Alive</option>
                    <option value="dead">Dead</option>
                    <option value="unknown">Unknown</option>
                </optgroup>
                <optgroup label="Gender">
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="genderless">Genderless</option>
                </optgroup>
            </select>
        </div>
    )
}

export { SearchFilter }
