import { useContext, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { Context } from '../context'

function CharacherCard() {
    const { darkTheme } = useContext(Context)
    const { id } = useParams()
    const [character, setCharacter] = useState(null)
    const { name, image, status, gender } = character.results
    useEffect(() => {
        fetch(`https://rickandmortyapi.com/api/character/${id.slice(1)}`)
            .then((response) => response.json())
            .then((data) => setCharacter(data))
    }, [id])

    return (
        <div className="character">
            <div className="picture">
                <img src={image} alt="" />
            </div>
            <div className={darkTheme ? 'description' : 'description-dark'}>
                <p className="country-name">Name: {name}</p>
                <p className="country-name">Status: {status}</p>
                <p className="country-name">Gender: {gender}</p>
            </div>
        </div>
    )
}

export { CharacherCard }
